mapper.insert:
INSERT /* MemberTrxMapper.insertMember */
  INTO MEMBER (
       MEMBER_NO
     , LOGIN_ID
     , NAME
     , PASSWORD
     , EMAIL
     , PHONE
     , POINTS
     , CREATED_AT
)
VALUES (
       #{memberNo}
     , #{loginId}
     , #{name}
     , #{password}
     , #{email}
     , #{phone}
     , COALESCE(#{points}, 0)
     , NOW()
)

mapper.update:
UPDATE /* MemberTrxMapper.updateMember */
       MEMBER
   SET NAME = #{name}
     , EMAIL = #{email}
     , PHONE = #{phone}
     , UPDATED_AT = NOW()
 WHERE MEMBER_NO = #{memberNo}

UPDATE /* MemberTrxMapper.updatePoints */
       MEMBER
   SET POINTS = #{points}
     , UPDATED_AT = NOW()
 WHERE MEMBER_NO = #{memberNo}

UPDATE /* MemberTrxMapper.updateMemberPoints */
       MEMBER
   SET POINTS = POINTS + #{amount}
     , UPDATED_AT = NOW()
 WHERE MEMBER_NO = #{memberNo}
