mapper.insert:
INSERT /* PaymentInterfaceTrxMapper.insertPaymentInterface */
  INTO PAYMENT_INTERFACE (
       PG_TYPE
     , TRANSACTION_TYPE
     , TRANSACTION_DATETIME
     , ORDER_NO
     , PAYMENT_NO
     , REQUEST_JSON
     , RESPONSE_JSON
     , RESULT_CODE
     , CREATED_AT
)
VALUES (
       #{pgType}
     , #{transactionType}
     , #{transactionDatetime}
     , #{orderNo}
     , #{paymentNo}
     , #{requestJson}
     , #{responseJson}
     , #{resultCode}
     , NOW()
)

mapper.update:
UPDATE /* PaymentInterfaceTrxMapper.updatePaymentInterfaceResponse */
       PAYMENT_INTERFACE
   SET RESPONSE_JSON = #{responseJson}
     , RESULT_CODE = #{resultCode}
     , UPDATED_AT = NOW()
 WHERE INTERFACE_SEQ = #{interfaceSeq}
