mapper.insert:
INSERT /* ProductTrxMapper.insertProduct */
  INTO PRODUCT (
       PRODUCT_NO
     , PRODUCT_NAME
     , PRICE
     , CREATED_AT
)
VALUES (
       #{productNo}
     , #{productName}
     , #{price}
     , NOW()
)

mapper.update:
UPDATE /* ProductTrxMapper.updateProduct */
       PRODUCT
   SET PRODUCT_NAME = #{productName}
     , PRICE = #{price}
     , UPDATED_AT = NOW()
 WHERE PRODUCT_NO = #{productNo}

mapper.delete:
DELETE /* ProductTrxMapper.deleteProduct */
  FROM PRODUCT
 WHERE PRODUCT_NO = #{productNo}
